{
    "data": {
        "sessionId": "100",
        "prompt": "Call getOperations and tell me what is available",
        "metadata": {
            "assistant": {
                "instructions":"Act really happy and have over the top happiness in your answers."
            ,"data":{
             "operations": [
            {
                "method": "POST",
                "name": "getOperations",
                "description": "Get a list of available operations for an assistant.",
                "id": "getOperations",
                "params": [
                    {
                        "name": "tag",
                        "description": "The optional tag to search for."
                    }
                ],
                "type": "custom",
                "url": "/ops/get",
                "includeAccessToken": true,
                "tags": [
                    "ops",
                    "default",
                    "all"
                ]
            },
            {
                "method": "POST",
                "name": "llmQueryDatasource",
                "description": "Query a datasource using the LLM. This is a much more extensive and robust, but expensive, search. It looks at the entire document and can be used when you need all information related to a topic",
                "id": "llmQueryDatasource",
                "params": [
                    {
                        "name": "id",
                        "description": "The ID of the datasource to use for the query."
                    },
                    {
                        "name": "query",
                        "description": "The 'query' or 'task' to use for the query."
                    }
                ],
                "type": "custom",
                "url": "/llm/query",
                "includeAccessToken": true,
                "tags": [
                    "llm",
                    "default",
                    "all"
                ]
            }
        ]}}
        }
    }
}
